mapper.delete:

	delete from basket
	where

	
		
		
			nonmember_no=#{nonmember_no}  
		
		
		
		
			member_no=#{member_no}  
		
		
	
	
	and item_no=#{item_no}

mapper.select:
SELECT a.item_no, e.photo, b.title, a.item_count, (a.item_count * c.price * 0.01) accmulate,(a.item_count * c.price) total_item_price FROM basket a, item b, item_detail c, item_option d, item_photo e WHERE
a.member_no=#{member_no}
a.nonmember_no=#{nonmember_no}
and a.item_no=b.no and b.no=c.item_no and c.item_no=d.item_no and d.item_no=e.item_no and a.option_no = d.no and e.is_main=1
